import React from 'react';
import  ReactDOM from "react-dom";
import './index.css'
import TodoForm from './TodoForm'
import Todo from './Todo'

 class TodoList extends React.Component{
    state = {
        todos : [],
        todoToShow: "all"
    }

    addTodo = todo => {
        this.setState({
            todos : [todo , ...this.state.todos]
        })


    }

    toggleComplete = id => {
         this.setState({
             todos:this.state.todos.map( todo => {
                 if(todo.id ==id){
                     return {
                         ...todo,
                         complete: !todo.complete
                     }
                 }
                 else{
                     return todo;
                 }
             })
         })
    }

    updateTodoToshow = (s) => {

        this.setState({
            todoToShow: s
        })
    };
 handleDeleteTodo = (id) => {
     this.setState({
         todos: this.state.todos.filter(todo => todo.id !== id)
     });
 };
     // removeAllTodoThatAreComplete = () => {
     //     this.setState({
     //         todos: this.state.todos.filter(todo => !todo.complete)
     //     });
    // }

     componentDidMount(){
         localStorage.getItem("todoItems") && this.setState({
       todos: JSON.parse(localStorage.getItem("todoItems"))

         });
     }


     componentWillUpdate(nextProps,nextState){
         localStorage.setItem("todoItems",JSON.stringify(nextState.todos));
         localStorage.setItem("todoToShow",nextState.todoToShow);

     }

    render(){
        let todos = [];

        if(this.state.todoToShow === "all"){
            todos =this.state.todos;
        }else if(this.state.todoToShow === "active"){
            todos = this.state.todos.filter(todo => !todo.complete);
        }else if(this.state.todoToShow === "complete"){
            todos = this.state.todos.filter(todo => todo.complete);
        }
        return(
            <div>
                <TodoForm onSubmit={this.addTodo}/>
                {todos.map(
                    todo => (
                        <Todo key={todo.id}
                              toggleComplete={() => this.toggleComplete(todo.id)}
                              onDelete = { () => this.handleDeleteTodo(todo.id)}
                              todo={todo} />

                    )
                )}
                <div className="todo-left">
                    TODOS left: {this.state.todos.filter(todo => !todo.complete).length}
                </div>
                <div>
                    <button  className="current-status" onClick={() => this.updateTodoToshow("all")} >ALL</button>
                    <button  className="current-status" onClick={() => this.updateTodoToshow("active")}>ACTIVE</button>
                    <button className="current-status" onClick={() => this.updateTodoToshow("complete")}>COMPLETE</button>
                </div>
                {/*{this.state.todos.some(todo => todo.complete)?*/}
                {/*( <div>*/}
                    {/*<button onClick={this.removeAllTodoThatAreComplete}>*/}
                        {/*remove all complete todos*/}
                    {/*</button>*/}
                {/*</div>) : null}*/}
            </div>
        );
    }
}
const destination = document.getElementById("container");
ReactDOM.render(<TodoList />,destination);
